query.main-query:
select count(*)
select t.loc_id, (case when t.loc_type = 1 then t.loc_name || ' (شهرستان ' || (select loc3.loc_name from emst_location loc3 where loc3.loc_id = t.loc_county_id) || ')' when t.loc_type = 2 then t.loc_name || ' (دهستان ' || (select loc3.loc_name from emst_location loc3 where loc3.loc_id = t.loc_district_id) || ')' when t.loc_type = 4 then 'استان ' || t.loc_name when t.loc_type = 5 then 'شهرستان ' || t.loc_name || ' (استان ' || (select loc3.loc_name from emst_location loc3 where loc3.loc_id = t.loc_township_id) || ')' else t.loc_name end) loc_name, 'LOC' loc_type
from ( select * from emst_location loc1 where loc1.loc_TYPE not in (3,4,6,7) and loc1.loc_STATE not in ('I') and (
(loc1.loc_type = 1)
(loc1.loc_type = 1 and loc1.loc_county_id in (select distinct loc2.loc_county_id from emst_location loc2 where loc2.loc_county_id is not null and loc2.loc_province_id = (select loc3.loc_province_id from emst_location loc3,emst_department ed where loc3.loc_id = ed.dep_location_id and ed.dep_id = {superDepartmentID}))) ) union select * from emst_location loc1 where loc1.loc_TYPE not in (3,4,6,7) and loc1.loc_STATE not in ('I') and ( (loc1.loc_type = 2 and loc1.loc_township_id in (select distinct loc2.loc_township_id from emst_location loc2 where loc2.loc_township_id is not null and loc2.loc_province_id = (select loc3.loc_province_id from emst_location loc3,emst_department ed where loc3.loc_id = ed.dep_location_id and ed.dep_id = {superDepartmentID})))
) ) t where t.loc_name like {name}

select count(*)
select t.loc_id, t.loc_name
from emst_location t where t.loc_TYPE ='4' and t.loc_STATE not in ('I') and t.loc_name like {name}

select count(*)
SELECT distinct DEP_ID, DEP_NAME
from EMST_ENROLLMENT_OFFICE join EMST_DEPARTMENT on eof_id = dep_id join emst_location on DEP_LOCATION_ID = loc_id where loc_province_id = {provinceId} and DEP_NAME like {officeName}
